import org.junit.Before;
import org.junit.Test;

import static org.junit.Assert.*;

public class AppTest {

    private App classUnderTest;

    @Before
    public void setUp() throws Exception {
        classUnderTest = new App();
    }

    @Test
    public void binarySum() throws Exception {
        //assertEquals("100",classUnderTest.binarySum("10","00"));
        assertEquals("101",classUnderTest.binarySum("10","11"));
        assertEquals("110",classUnderTest.binarySum("11","11"));
    }
    @Test
    public void binarySumDifferentSizeInputs() throws Exception {
        //assertEquals("100",classUnderTest.binarySum("10","00"));
        assertEquals("10011",classUnderTest.binarySum("10000","11"));
        assertEquals("11011",classUnderTest.binarySum("1100","1111"));
    }



    @Test
    public void testCanSumPlaces() {
        App.BitResult result = classUnderTest.sumPlace((byte)'0', (byte)'0', (byte)'0');
        assertEquals(new App.BitResult((byte)'0', (byte)'0'), result);

        result = classUnderTest.sumPlace((byte)'1', (byte)'0', (byte)'0');
        assertEquals(new App.BitResult((byte)'1', (byte)'0'), result);

        result = classUnderTest.sumPlace((byte)'1', (byte)'1', (byte)'0');
        assertEquals(new App.BitResult((byte)'0', (byte)'1'), result);

        result = classUnderTest.sumPlace((byte)'0', (byte)'0', (byte)'0');
        assertEquals(new App.BitResult((byte)'0', (byte)'0'), result);

        result = classUnderTest.sumPlace((byte)'0', (byte)'0', (byte)'1');
        assertEquals(new App.BitResult((byte)'1', (byte)'0'), result);

        result = classUnderTest.sumPlace((byte)'0', (byte)'1', (byte)'1');
        assertEquals(new App.BitResult((byte)'0', (byte)'1'), result);
    }
}
